$(function() {
    a();
    $(window).resize(function() {
        a()
    });
    function a() {
        var d = $("body").width();
        $(".cartoonWapper").width(d - 20);
        $(".footer").width(d - 60);
        var e = $(".cartoonNav>a.active").innerWidth(),
        c = $(".cartoonNav>a.active").offset().left,
        b = $(".cartoonNav").offset().left;
        $(".cartoonLine").css({
            "width": e,
            "left": c - b
        })
    }
    $(".cartoonNav>a").click(function() {
        var f = $(this),
        b = f.innerWidth(),
        e = $(".cartoonLine"),
        c = $(".cartoonNav").offset().left,
        d = $(this).offset().left;
        if (!f.hasClass("active")) {
            f.addClass("active").siblings().removeClass("active");
            e.css({
                "width": b + 50,
                "left": d - c
            });
            setTimeout(function() {
                e.width(b)
            },
            500)
        } else {
            e.css({
                "width": b,
                "left": d - c
            })
        }
    });
    new Swiper(".cartoon-Swiper", {
        spaceBetween: 5,
        centeredSlides: true,
        loop: true,
        observer: true,
        observeParents: true,
        autoplay: {
            delay: 3500,
            disableOnInteraction: false,
        },
        pagination: {
            el: ".cartoonRight",
            clickable: true,
        },
    });
    new Swiper(".swiper-Hot", {
        spaceBetween: 5 + "%",
        slidesPerView: 3,
        loop: true,
        observer: true,
        observeParents: true,
        autoplay: {
            delay: 5000,
            disableOnInteraction: false,
        },
    })
});