$(function() {
    c();
    $(window).resize(function() {
        c()
    });
    var a = $(".listBindNav .active").index();
    function c() {
        var g = $("body").width();
        var f = $(".listBindNav>a.active").offset().left,
        d = $(".listBindNav>a.active").innerWidth(),
        e = $(".listBindNav").offset().left;
        $(".listline").css({
            "left": f - e,
            "width": d
        })
    }
    $(".listBox").focus(function() {
        $(this).css("overflow-y", "auto")
    });
    var b = new Swiper(".swiper-Rank", {
        spaceBetween: 0,
        autoHeight: true,
        observer: true,
        observeParents: true,
        initialSlide: a,
        on: {
            slideChangeTransitionStart: function() {
                console.log(this.activeIndex);
                $(".listBindNav>a").removeClass("active");
                $(".listBindNav>a").eq(this.activeIndex).addClass("active");
                $(".listBox").removeClass("active");
                $(".listBox").eq(this.activeIndex).addClass("active");
                c()
            }
        }
    });
    $(".listBindNav>a").click(function() {
        var i = $(this),
        e = i.innerWidth(),
        h = $(".listline"),
        d = $(".listBindNav>a").index($(this)),
        f = $(".listBindNav").offset().left,
        g = $(this).offset().left;
        if (!i.hasClass("active")) {
            i.addClass("active").siblings().removeClass("active");
            h.css({
                "width": e + 10,
                "left": g - f
            });
            setTimeout(function() {
                h.width(e)
            },
            500);
            b.slideTo(d, 1000, false);
            $(".listBox").removeClass("active");
            $(".listBox").eq(d).addClass("active")
        } else {
            h.css({
                "width": e,
                "left": g - f
            })
        }
    })
});