var page = 1,
is_end = false;
getData();
$(window).scroll(function() {
    var b = $(this).scrollTop();
    var a = $(document).height();
    var c = $(this).height();
    if (b + c == a) {
        if (!is_end) {
            getData()
        }
    }
});
function getData() {
    if (!is_end) {
        is_end = true;
        data["page"] = page;
        ajaxPost("", data,
        function(b) {
            var a = 0;
            if (b) {
                $.each(b,
                function(c, d) {
                    a++;
                    var e = "";
                    e += "<li>";
                    e += '<a href="' + d.info_url + '">';
                    e += "<div>";
                    e += '<img src="' + d.cover + '" alt="">';
                    e += "</div>";
                    e += '<h4 class="over">' + d.name + "</h4>";
                    e += '<p class="over">' + d.summary + "</p>";
                    e += "</a>";
                    e += '<div class="buyMoney">';
                    e += "<span>";
                    e += "<em>¥" + d.money + "</em>";
                    e += "<p>" + d.buy_num + "人购买</p>";
                    e += "</span>";
                    e += '<a href="' + d.buy_url + '">立即购买</a>';
                    e += "</div>";
                    e += "</li>";
                    $(".mallList>ul").append(e)
                })
            }
            if (a >= 10) {
                page++;
                is_end = false
            }
        })
    }
};