function showSign() {
    layMsg("您尚未登录");
    var url = U("User/login");
    window.location.href=url;
}

function doSign(){
	$.get('http://'+document.domain+'/index/index/dosign',function(res){
		if(res.code=='0'){
			layMsg(res.data);
			setTimeout( function(){
			     window.location.reload();
			}, 3* 1000 );
			
		}else{
			layMsg(res.data);
		}
	})
}

