function doSetAuto(e) {
    var f = $(e).children("div").children("input"),
        a = $(e).children("div").children("span"),
        g = $(e).children("div").children("span").children("i");
    var c = "no";
    if (!$(e).hasClass("active")) {
        c = "yes"
    }
    var d = {
        is_on: c
    };
    var b = U("User/setAuto");
    if (b) {
        ajaxPost(b, d, function () {
            if (c === "yes") {
                $(e).addClass("active");
                f.prop("checked", true);
                g.css("left", 28);
                a.addClass("active")
            } else {
                $(e).removeClass("active");
                f.prop("checked", false);
                g.css("left", -2);
                a.removeClass("active")
            }
        })
    }
}
