function doFeedback() {
    var c = $("#content").val();
    if (c.length == 0 || c.length > 200) {
        layMsg("请输入200个字符以内的反馈内容");
        return false
    }
    var a = $("#phone").val();
    if (a) {
        var b = /^1[3456789][0-9]{9}$/;
        if (!b.test(a)) {
            layMsg("手机号格式不正确");
            return false
        }
    }
    var d = {
        content: c,
        phone: a
    };
    ajaxPost("", d,
    function() {
        layMsg("提交成功");
        setTimeout(function() {
            var e = U("feedback");
            window.location.href = e
        },
        1000)
    })
};