function dopost(){
    //头像
    cover=$('.hideval').val();
    //昵称
    nickname=$('#nickname').val();
    //性别
    sex=$("select option:selected").val();
    var a=U("upUserinfo");
    $.post(
        //1 异步提交的urL
        a,
        //2 form表单以键值对形式传输
        {headimgurl:cover,nickname:nickname,sex:sex},
        //3 访问后成功的回调函数
        function (data) {
            window.location.href = "/index/User/index";
        }
    );
}
$(function () {
    a();
    $(window).resize(function () {
        a()
    });
    function a() {
        var b = $("body").width();
        $(".footer").width(b - 60)
    }
});