var page = 1,
is_end = false;
getData();
function getData() {
    if (!is_end) {
        is_end = true;
        ajaxPost("", {
            page: page
        },
        function(b) {
            var a = 0;
            if (b) {
                $.each(b,
                function(d, e) {
                    a++;
                    var f = "";
                    f += "<li>";
                    f += '<div class="messagesCenter">';
                    f += '<img src="/static/index/images/inform.png" alt="">';
                    f += '<div class="messagesInform">';
                    f += "<h4>系统公告</h4>";
                    f += "<p>" + e.create_time + "</p>";
                    f += "<h5>" + e.title + "</h5>";
                    f += '<div class="messagesCount">';
                    f += e.content;
                    f += "</div>";
                    f += "</div>";
                    f += "</div>";
                    f += "</li>";
                    $(".messages>ul").append(f)
                })
            } else {
                if (page === 1) {
                    var c = "";
                    c += '<div class="searchTs">';
                    c += '<img src="/static/index/images/searchBg.png" alt="">';
                    c += "<p>抱歉亲亲～</p>";
                    c += "<p>没有相关内容</p>";
                    c += "</div>";
                    $(".messages").html(c)
                }
            }
            if (a >= 10) {
                page++;
                is_end = false
            }
        })
    }
}
$(window).scroll(function() {
    var b = $(this).scrollTop();
    var a = $(document).height();
    var c = $(this).height();
    if (b + c == a) {
        if (!is_end) {
            getData()
        }
    }
});