var page = 1;
is_end = false;
getData();
function getData() {
    if (!is_end) {
        is_end = true;
        ajaxPost("", {
            page: page
        },
        function(b) {
            var a = 0;
            if (b) {
                $.each(b,
                function(d, e) {
                    a++;
                    var f = "";
                    f += '<div class="orderCount marginBot">';
                    f += "<p>" + e.status_name + "</p>";
                    f += '<div class="orderPos">';
                    f += '<div class="orderImg" >';
                    f += '<a href="' + e.url + '">';
                    f += '<img src="' + e.cover + '" alt="">';
                    f += "</a>";
                    f += "</div>";
                    f += '<div class="orderFont">';
                    f += "<h4>";
                    f += '<a href="' + e.url + '">';
                    f += e.name;
                    f += "</a>";
                    f += "</h4>";
                    f += '<p class="over" style="height:20px ">' + e.summary + "</p>";
                    f += '<span class="number_sl">X' + e.count + "</span>";
                    f += '<div class="total">';
                    f += "共" + e.count + "件商品，合计：¥ <span>" + e.money + "</span>";
                    f += "</div>";
                    f += "</div>";
                    f += "</div>";
                    f += "</div>";
                    $(".orderBox").append(f)
                })
            } else {
                if (page === 1) {
                    var c = "";
                    c += '<div class="searchTs">';
                    c += '<img src="/static/index/images/searchBg.png" alt="">';
                    c += "<p>抱歉亲亲～</p>";
                    c += "<p>没有相关内容</p>";
                    c += "</div>";
                    $(".orderBox").html(c)
                }
            }
            if (a >= 10) {
                page++;
                is_end = false
            }
        })
    }
}
$(window).scroll(function() {
    var b = $(this).scrollTop();
    var a = $(document).height();
    var c = $(this).height();
    if (b + c == a) {
        if (!is_end) {
            getData()
        }
    }
});
$(function() {
    a();
    $(window).resize(function() {
        a()
    });
    function a() {
        var b = $("body").width();
        $(".footerBook").width(b)
    }
});