var page=1,is_end=false;getData();
$(window).scroll(function(){
    var b=$(this).scrollTop();
    var a=$(document).height();
    var c=$(this).height();
    if(b+c==a){
        if(!is_end){
            getData()
        }
    }
});
function getData(){
    if(!is_end){
        is_end=true;
        data["page"]=page;
        ajaxPost("",data,function(b){
            var a=0;if(b){
                $.each(b,function(d,e){
                    a++;
                    var f="";f+='<div class="rankList">';
                    if(e.rank<4){
                        f+='<div class="rankNumber hotImg">';
                        f+='<img src="/static/index/images/hot'+e.rank+'.png" alt="">';
                        f+="</div>"
                    }else{
                        f+='<div class="rankNumber">'+e.rank+"</div>"
                    }
                    f+='<div class="rankNr">';
                    f+='<div class="cartoonRecommend padding1" >';
                    f+='<div class="recommendList margin0">';
                    f+="<ul>";
                    f+='<li>';
                    f+='<div class="recommendPos">';
                    f+='<div class="recommendHotImg wx_dingwei">';
                    f+='<a href="'+e.info_url+'"><img src="'+e.cover+'" alt=""></a>';
                    if(e.free_type=="2"){
                        f+='<div class="isvip">'
                        f+='<img src="/static/index/images/wx_vip.png" style="width: 100% !important;height: auto !important;">'
                        f+='</div>';
                    }
                    f+="</div>";
                    f+='<div class="recommendFont">';
                    f+='<h4 class="over"><a href="'+e.info_url+'">'+e.name+"</a></h4>";
                    f+='<p class="over2">';
                    f+='<a href="'+e.info_url+'">'+e.summary+"</a>";
                    f+="</p>";
                    if(e.category){
                        f+='<div class="historyTag">';
                        var c=e.category.length;
                        for(var d=0;d<c;d++){
                            f+='<a href="javascript:void(0);">'+e.category[d]+"</a>"
                        }
                        f+="</div>"
                    }
                    f+='<div class="historyNumber">';
                    f+="<div>";f+="<span>";f+='<img src="/static/index/images/flame.png" alt="">';
                    f+="<em>"+e.hot_num+"</em>";
                    f+="</span>";
                    f+="</div>";
                    f+="<div>";
                    f+="</div>";
                    f+="</div>";
                    f+="</div>";
                    f+="</div>";
                    f+="</li>";
                    f+="</ul>";
                    f+="</div>";
                    f+="</div>";
                    f+="</div>";
                    f+="</div>";
                    $(".rankCount").append(f)
                })
            }
            if(a>=10){
                page++;is_end=false
            }
        })
    }
};